package Assignment;

public class compound {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 10;
		 a=10;
		 System.out.println(a);
		 
		 a+=10;						// compound (a=a+10)
		 System.out.println(a);
		 
		 a-=10;						// compound (a=a-10)
		 System.out.println(a);
		 
		 a*=10;						//compound (a=a*10)
		 System.out.println(a);
		 
		 a/=10;						//compound (a=a/10)
		 System.out.println(a);
		 
	//	 a%=10;
	//	 System.out.println(a);
	}

}
